import React, { useState } from 'react';

// Styled JSX for consistent styling
const styles = `
  .full-page {
    height: 100vh;
    width: 100vw;
    background-color: #1c794c;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 0;
  }
  
  .game-container {
    max-width: 1200px;
    width: 100%;
    padding: 24px;
    box-sizing: border-box;
  }
  
  .game-header {
    width: 100%;
    position: relative;
  }
  
  .game-image {
    width: 100%;
    height: auto;
    display: block;
  }
  
  .game-title-section {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-top: 24px;
  }
  
  .game-title {
    font-family: 'DM Serif Display', -apple-system, Roboto, Helvetica, sans-serif;
    color: #FFF;
    font-size: 2em;
  }
  
  .game-selection-section {
    display: flex;
    justify-content: center;
    align-items: center;
    margin-top: 24px;
  }
  
  .choice-heading {
    color: var(--lighter-green, #f2f9f6);
    font-weight: bold;
    margin-right: 12px;
    font-size: 1.5em;
  }
  
  .game-choices-container {
    margin-top: 24px;
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
    gap: 15px;
  }
  
  .game-image-choice {
    width: 100px;
    height: 100px;
    cursor: pointer;
    transition: transform 0.2s ease-in-out;
  }
  
  .selected {
    border: 3px solid black;
  }
  
  .confirm-selection {
    margin-top: 24px;
    padding: 12px 24px;
    background-color: #000;
    color: #FFF;
    cursor: pointer;
    text-align: center;
    width: auto;
    border-radius: 12px;
  }
`;

function GameChoice({ src, alt, onSelect, isSelected }) {
  return (
    <img
      loading="lazy"
      src={src}
      alt={alt}
      className={`game-image-choice ${isSelected ? 'selected' : ''}`}
      onClick={onSelect}
      style={{ aspectRatio: '1', objectFit: 'cover' }}
    />
  );
}

function TrainSelector() {
  const [selectedTrain, setSelectedTrain] = useState('');
  
  const gameChoices = [
    { src: "https://cdn.builder.io/api/v1/image/assets/TEMP/d737771a839e9d3381cf8be0888aafeb9423dad94e31ce6e6b57702a2eb9bb23", alt: "Train Choice 1" },
    { src: "https://cdn.builder.io/api/v1/image/assets/TEMP/6f86f2b40e7a9d4e455f9a504dc5e366503ace6cf85fc3fb36aac70ac88a8fdf", alt: "Train Choice 2" },
    { src: "https://cdn.builder.io/api/v1/image/assets/TEMP/5ec4c3ceb8e14edecf8b637152c22aeb4571947ce4e06158fb90c3aa98a3f917", alt: "Train Choice 3" },
    { src: "https://cdn.builder.io/api/v1/image/assets/TEMP/b113a030456c013a7e21d15ec68b8ef946e9a436bdd585825811f93c41853999", alt: "Train Choice 4" },
    { src: "https://cdn.builder.io/api/v1/image/assets/TEMP/19af73f19f2af54ce737c599541168e9050396e728f163bd56c5c822021bf5ac", alt: "Train Choice 5" },
    { src: "https://cdn.builder.io/api/v1/image/assets/TEMP/2e707c1c15ef3106cc95045c56346e6166b027d11a3d59268444fa4919181093", alt: "Train Choice 6" },
  ];
  
  return (
    <>
      <style jsx>{styles}</style>
      <div className="full-page">
        <div className="game-container">
          <div className="game-header">
            <img
              loading="lazy"
              src="https://cdn.builder.io/api/v1/image/assets/TEMP/8dbca933ffcd089f07077f037b47f9d35112fe8982e429140eb2217b3c26466f?apiKey=7293d20271ec4f72a58fe358903b4fd6&"
              alt="Game Background"
              className="game-image"
            />
            <div className="game-title-section">
              <div className="game-title">Mexican Train Dominoes</div>
            </div>
          </div>
          <div className="game-selection-section">
            <span className="choice-heading">Choose your train</span>
          </div>
          <div className="game-choices-container">
            {gameChoices.map(choice => (
              <GameChoice
                key={choice.src}
                src={choice.src}
                alt={choice.alt}
                onSelect={() => setSelectedTrain(choice.src)}
                isSelected={selectedTrain === choice.src}
              />
            ))}
          </div>
          <div
            className="confirm-selection"
            onClick={() => alert(`You've selected: ${selectedTrain}`)}
          >
            Confirm Selection
          </div>
        </div>
      </div>
    </>
  );
}

export default TrainSelector;
