import React from "react";

const ImageWithAlt = ({ src, alt, className }) => (
  <img loading="lazy" src={src} alt={alt} className={className} />
);

const SectionTitle = ({ children }) => (
  <div className="section-title">{children}</div>
);

const SelectionConfirmButton = ({ children }) => (
  <div className="confirm-selection">{children}</div>
);

function TrainSelectionApp() {
  const images = [
    { src: "https://cdn.builder.io/api/v1/image/assets/TEMP/d737771a839e9d3381cf8be0888aafeb9423dad94e31ce6e6b57702a2eb9bb23?apiKey=7293d20271ec4f72a58fe358903b4fd6&", alt: "Train 1" },
    { src: "https://cdn.builder.io/api/v1/image/assets/TEMP/6f86f2b40e7a9d4e455f9a504dc5e366503ace6cf85fc3fb36aac70ac88a8fdf?apiKey=7293d20271ec4f72a58fe358903b4fd6&", alt: "Train 2" },
    { src: "https://cdn.builder.io/api/v1/image/assets/TEMP/5ec4c3ceb8e14edecf8b637152c22aeb4571947ce4e06158fb90c3aa98a3f917?apiKey=7293d20271ec4f72a58fe358903b4fd6&", alt: "Train 3" },
    { src: "https://cdn.builder.io/api/v1/image/assets/TEMP/b113a030456c013a7e21d15ec68b8ef946e9a436bdd585825811f93c41853999?apiKey=7293d20271ec4f72a58fe358903b4fd6&", alt: "Train 4" },
    { src: "https://cdn.builder.io/api/v1/image/assets/TEMP/19af73f19f2af54ce737c599541168e9050396e728f163bd56c5c822021bf5ac?apiKey=7293d20271ec4f72a58fe358903b4fd6&", alt: "Train 5" },
    { src: "https://cdn.builder.io/api/v1/image/assets/TEMP/2e707c1c15ef3106cc95045c56346e6166b027d11a3d59268444fa4919181093?apiKey=7293d20271ec4f72a58fe358903b4fd6&", alt: "Train 6" }
  ];

  return (
    <>
      <section className="app-container">
        <ImageWithAlt src="https://cdn.builder.io/api/v1/image/assets/TEMP/8dbca933ffcd089f07077f037b47f9d35112fe8982e429140eb2217b3c26466f?apiKey=7293d20271ec4f72a58fe358903b4fd6&" alt="Mexican Train Dominoes Game" className="background-image" />
        <div className="content-wrapper">
          <SectionTitle>Mexican Train Dominoes</SectionTitle>
          <ImageWithAlt src="https://cdn.builder.io/api/v1/image/assets/TEMP/454567658b547d84431fbbdf8224a75d18594956415276dd425887318929e8c0?apiKey=7293d20271ec4f72a58fe358903b4fd6&" alt="Icon" className="icon" />
          <div className="train-selection">
            <ImageWithAlt src="https://cdn.builder.io/api/v1/image/assets/TEMP/025477eabf784886eadb60c6ba12a0fec11935a51338f9020aed34c374c076aa?apiKey=7293d20271ec4f72a58fe358903b4fd6&" alt="Choose your train" className="choose-train" />
            <SectionTitle>Choose your train</SectionTitle>
            <ImageWithAlt src="https://cdn.builder.io/api/v1/image/assets/TEMP/4f3dfe0c2a2570e365de2a0fd6e749da98786ab34b67c024779968de787f3a05?apiKey=7293d20271ec4f72a58fe358903b4fd6&" alt="Icon" className="icon" />
          </div>
          <div className="train-images-container">
            {images.map((image, index) => (
              <ImageWithAlt key={index} src={image.src} alt={image.alt} className="train-image" />
            ))}
          </div>
          <SelectionConfirmButton>Confirm Selection</SelectionConfirmButton>
        </div>
      </section>
      <style jsx>{`
        .app-container {
          background-color: #1c794c;
          display: flex;
          flex-direction: column;
          justify-content: center;
          padding: 44px 0;
        }
        .content-wrapper {
          display: flex;
          flex-direction: column;
          position: relative;
          min-height: 1029px;
          width: 100%;
          padding: 0 64px 50px;
        }
        .background-image {
          position: absolute;
          inset: 0;
          height: 100%;
          width: 100%;
          object-fit: cover;
          object-position: center;
        }
        .content-wrapper > * {
          position: relative;
          z-index: 10;
          display: flex;
          gap: 20px;
        }
        .section-title {
          font-size: 72px;
          color: #fff;
          font-weight: 400;
        }
        .icon {
          width: 80px;
          aspect-ratio: 1;
          object-fit: contain;
        }
        .train-selection, .train-images-container {
          align-items: start;
          font-size: 80px;
          color: #f2f9f6;
        }
        .train-image {
          width: 127px;
          aspect-ratio: 0.69;
          object-fit: contain;
        }
        .confirm-selection {
          justify-content: center;
          border-radius: 20px;
          background-color: #1c794c;
          color: #fff;
          padding: 33px 15px;
          font: 500 48px Roboto, sans-serif;
          cursor: pointer;
        }
        @media (max-width: 991px) {
          .content-wrapper {
            padding: 0 20px;
          }
          .section-title, .train-selection, .train-images-container {
            font-size: 40px;
          }
          .confirm-selection {
            font-size: 40px;
            white-space: initial;
          }
        }
      `}</style>
    </>
  );
}

export default TrainSelector;
