import React from 'react';

const GameTitle = ({ title }) => {
  return <h1 className="game-title">{title}</h1>;
};

const GameLogo = ({ src, alt }) => {
  return <img src={src} alt={alt} className="game-logo" loading="lazy" />;
};

function NavBar() {
  return (
    <>
      <header className="header">
        <GameTitle title="Mexican Train Dominoes" />
        <GameLogo src="https://cdn.builder.io/api/v1/image/assets/TEMP/454567658b547d84431fbbdf8224a75d18594956415276dd425887318929e8c0?apiKey=7293d20271ec4f72a58fe358903b4fd6&" alt="Mexican Train Dominoes Game Logo" />
      </header>
      <style jsx>{`
        .header {
          display: flex;
          justify-content: space-between;
          gap: 20px;
          font-size: 72px;
          color: #fff;
          font-weight: 400;
        }
        @media (max-width: 991px) {
          .header {
            flex-wrap: wrap;
            font-size: 40px;
          }
        }
        .game-title {
          font-family: DM Serif Display, -apple-system, Roboto, Helvetica, sans-serif;
          flex-grow: 1;
          flex-basis: auto;
        }
        @media (max-width: 991px) {
          .game-title {
            max-width: 100%;
            font-size: 40px;
          }
        }
        .game-logo {
          width: 80px;
          aspect-ratio: 1.01;
          object-fit: cover;
          object-position: center;
          margin: auto 0;
        }
      `}</style>
    </>
  );
}

export default NavBar;
